window.addEventListener("load",function(){for(var e=document.querySelectorAll(".check"),t=document.querySelector("#nickname"),n=0;n<e.length;n++)e[n].addEventListener("click",function(){var n=this.textContent;console.log(n),"No"==n?(t.style.display="block",console.log(t)):t.style.display="none";for(var o=0;o<e.length;o++)e[o].classList.remove("active");this.classList.add("active")})});